import "./Login_Registrarse.css";
import React, { useState } from "react";

const Registrarse = ({ setView }) => {
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    password: ""
  });
  const [errors, setErrors] = useState({});

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUserData({
      ...userData,
      [name]: value
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const validationErrors = validateForm(userData);
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      console.log("Datos del formulario:", userData);
      // lógica para enviar los datos del formulario al servidor
    }
  };

  const validateForm = (data) => {
    let errors = {};

    if (!data.username.trim()) {
      errors.username = "El nombre de usuario es requerido";
    }

    if (!data.email.trim()) {
      errors.email = "El correo electrónico es requerido";
    } else if (!/\S+@\S+\.\S+/.test(data.email)) {
      errors.email = "El correo electrónico ingresado no es válido";
    }

    if (!data.password.trim()) {
      errors.password = "La contraseña es requerida";
    } else if (data.password.trim().length < 6) {
      errors.password = "La contraseña debe tener al menos 6 caracteres";
    }

    return errors;
  };

  const handleLoginLinkClick = () => {
    setView("login");
  };

  return (
    <div className="bodyregister">
      <form className="formRegister" onSubmit={handleSubmit}>
        <h2>Registrarse</h2>
        <div className="formGroup">
          <label htmlFor="username">Nombre de usuario</label>
          <input 
            type="text" 
            id="username" 
            name="username" 
            value={userData.username} 
            onChange={handleChange} 
          />
          {errors.username && <span className="errorMessage">{errors.username}</span>}
        </div>
        <div className="formGroup">
          <label htmlFor="email">Correo electrónico</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            value={userData.email} 
            onChange={handleChange} 
          />
          {errors.email && <span className="errorMessage">{errors.email}</span>}
        </div>
        <div className="formGroup">
          <label htmlFor="password">Contraseña</label>
          <input 
            type="password" 
            id="password" 
            name="password" 
            value={userData.password} 
            onChange={handleChange} 
          />
          {errors.password && <span className="errorMessage">{errors.password}</span>}
        </div>
        <button type="submit">Registrarse</button>
      </form>
      <div className="loginLink" onClick={handleLoginLinkClick}>
        ¿Ya tienes una cuenta? Inicia sesión aquí.
      </div>
    </div>
  );
};

export default Registrarse;
